using UnityEngine;using System.Collections;public class ReticleController : MonoBehaviour {    public float reticleMaxDistance_ = 50f;    Transform mainCameraTransform_;    Ray objectCheckRay_;    RaycastHit hitRay_;    void Awake() {        mainCameraTransform_ = transform.parent;    }    // 最長距離内に障害物があったら照準をそこに合わす。なければ最長距離に照準を置く    // この処理はRayを使って行う    void Update() {        objectCheckRay_.origin = mainCameraTransform_.position;                Vector3 maxVector3 = mainCameraTransform_.transform.position + (mainCameraTransform_.forward * reticleMaxDistance_);        Vector3 angle = maxVector3 - mainCameraTransform_.transform.position;        angle = angle.normalized;        objectCheckRay_.direction = angle;        Debug.DrawLine(objectCheckRay_.origin, maxVector3, Color.blue);        if (Physics.Raycast(objectCheckRay_, out hitRay_, reticleMaxDistance_)) {            transform.position = hitRay_.point;        }        else {            transform.position = maxVector3;        }    }}