using UnityEngine;using System.Collections;public class PlayerMovement : MonoBehaviour {    public float speed_ = 6.00f;    Transform cameraTransform_;    Rigidbody playerRigidbody_;    void Awake() {        cameraTransform_ = GameObject.FindGameObjectWithTag("MainCamera").transform;        playerRigidbody_ = GetComponent<Rigidbody>();    }	void FixedUpdate() {        float h = Input.GetAxis("Horizontal");        float w = Input.GetAxis("Vertical");        Move(h, w);        Turning();    }    void Move(float h, float w) {        // ワールド座標時の移動量に置き換え        Vector3 worldMove = new Vector3(h, 0.0f, w);        // カメラの向きに合わせて移動を決定        Vector3 movement = cameraTransform_.rotation * worldMove;        movement = movement.normalized * speed_ * Time.deltaTime;        playerRigidbody_.MovePosition(transform.position + movement);            }    void Turning() {        playerRigidbody_.MoveRotation(cameraTransform_.rotation);    }}